import style from "./Header.module.scss";
import { Link } from "react-router-dom";
import { useState, useRef } from "react";

const Header = () => {
  const [activeMenu, setActiveMenu] = useState<boolean>(false);
  const menu = useRef<HTMLElement>(null);

  return (
    <header className={style.header}>
      <div
        className={style.burgerMenu}
        onClick={() => setActiveMenu(!activeMenu)}
      >
        <span></span>
        <span></span>
        <span></span>
      </div>

      <nav ref={menu}>
        <ul className={`${style.menu} ${activeMenu ? "" : style.menuActive}`}>
          <li>
            <Link to="/" onClick={() => console.log(activeMenu)}>
              Главная
            </Link>
          </li>
          <li>
            <Link to="/">Как пользоваться?</Link>
          </li>
          <li>
            <Link to="/">Настройки</Link>
          </li>
          <li>
            <Link to="/Register">Профиль</Link>
          </li>
          <li>
            <Link to="/">О приложении</Link>
          </li>
        </ul>
      </nav>

      <h1 className={style.title}>
        <Link to="/">arlezu</Link>
      </h1>

      <Link to="/" className={style.settingsButton}></Link>
    </header>
  );
};

export { Header };
