import './App.css';
import { useState } from 'react';

function App() {

  let [ cardsArray, setCardsArray ] = useState(cardsContent);

  let changeRepeatTime = (id, days) => {
    setCardsArray(cardsArray.map(pr => pr.id == id ? pr.repeatTime = days : pr = pr))
  }

  return (
    <div className='wrapper'>
      <div className="app">
        <header className="app__header">
          <h1>LaPalabra - Слова</h1>        
        </header>
        <main>

        </main>
      </div>
    </div>
  );
}

let cardsContent = [
  {
    id: 1,
    word: 'casa',
    translation: 'house',
    sentence: 'mi casa es blanca',
    repeatTime: 0,
  },
  {
    id: 2,
    word: 'ojo',
    translation: 'eye',
    sentence: 'Diego tiene los ojos verdes',
    repeatTime: 0,
  },
  {
    id: 3,
    word: 'vida',
    translation: 'life',
    sentence: 'viva la vida!',
    repeatTime: 0,
  },
]

export default App;
