import './scss/App.scss';
import { useState } from 'react';
import Header from './components/Header/Header';
import CardRouter from './components/CardRouter/CardRouter';
import CardList from './components/CardList/CardList';
import { Routes, Route } from 'react-router-dom';

import Card from './card';

function App() {

  let [ cardsArray, setCardsArray ] = useState(cardsContent);
  let [ step, setStep ] = useState(0);
  let [ onRoute, setOnRoute] = useState(false);

  let changeRepeatTime = (id, days) => {
    setCardsArray(cardsArray.map(card => card.id === id ? card.repeatTime = days : card));
  }

  return (

    <div className='wrapper'>
      <div className="app">
        <Header />
        <main>
        <a href='/card'>Учить</a>
          <Routes>
            <Route path="/CardRouter" component={<CardRouter cards={cardsArray} step={step} setStep={setStep} setOnRoute={setOnRoute}/>}/>
            <Route path='/CardList' component={<CardList cards={cardsArray} step={step} setStep={setStep} onRoute={onRoute} setOnRoute={setOnRoute}/>}/>
            <Route path="/card" component={<Card />}/>
          </Routes>
          {/* 
          onRoute ? 
            <CardRouter cards={cardsArray} step={step} setStep={setStep} setOnRoute={setOnRoute}/> :
            <CardList cards={cardsArray} step={step} setStep={setStep} onRoute={onRoute} setOnRoute={setOnRoute}/> 
  */}
        </main>
        </div>
    </div>

  );
}

export default App;

