export default function({ card, step, setStep, onRoute, setOnRoute }) {

  let startRoute = () => {
    setStep(card.id - 1);
    setOnRoute(true);
  }

  return (
        <div className='card' key={ card.id } onClick={startRoute}>
          <div className='card__img'>
            <img src={ card.imgSrc }/>
          </div>
          <div className='card__text'>
              <p className='card__word'>{ card.word }</p>
              <p className='card__wordTranslation'>{ card.wordTranslation }</p>
          </div>
        </div>
  );
}
