import './App.css';
import { useState } from 'react';
import Header from './components/Header';
import CardRouter from './components/CardRouter';
import CardList from './components/CardList';

function App() {

  let [ cardsArray, setCardsArray ] = useState(cardsContent);
  let [ step, setStep ] = useState(0);
  let [ onRoute, setOnRoute] = useState(false);

  let changeRepeatTime = (id, days) => {
    setCardsArray(cardsArray.map(card => card.id === id ? card.repeatTime = days : card));
  }

  return (
    <div className='wrapper'>
      <div className="app">
        <Header />
        <main>
          { 
          onRoute ? 
            <CardRouter cards={cardsArray} step={step} setStep={setStep} setOnRoute={setOnRoute}/> :
            <CardList cardsArray={cardsArray} step={step} setStep={setStep} onRoute={onRoute} setOnRoute={setOnRoute}/> 
          }
          { console.log(onRoute) }
        </main>
      </div>
    </div>
  );
}

let cardsContent = [
  {
    id: 1,
    word: 'տուն',
    wordTranslation: 'house',
    phonetics: 'tun',
    sentence: 'Yo vivo en esa casa blanca',
    sentenceTranslation: 'Я живу в том белом доме',
    imgSrc: './img/house.jpg',
    repeatTime: 0,
  },
  {
    id: 2,
    word: 'աչք',
    wordTranslation: 'eye',
    phonetics: 'ach’k’',
    sentence: 'Diego tiene los ojos verdes',
    sentenceTranslation: 'Да здравствует жизнь!',
    imgSrc: './img/eye.jpg',
    repeatTime: 0,
  },
  {
    id: 3,
    word: 'կյանքը',
    wordTranslation: 'life',
    phonetics: '',
    sentence: 'Viva la vida!',
    sentenceTranslation: 'Да здравствует жизнь!',
    imgSrc: './img/life.jpg',
    repeatTime: 0,
  },
]

export default App;
