import styles from './CardRepeater.module.scss'

export default function ({ cards, step }) {

  const cardsForRepeat = [];

  cards.map(card => {
    if (card.repeatTime > -1)
      cardsForRepeat = [...cardsForRepeat, card.id];
  });

  return (
    <div>
      <div className={styles.card}>
        <div className={styles.textTop}>
          <p className={styles.word}>{cards[step].word}</p>
          <p className={styles.wordTranslation}>{cards[step].wordTranslation}</p>
        </div>

        <div className={styles.img}>
          {<img src={cards[step].imgSrc} />}
        </div>

        <div className={styles.textBottom}>
          <p className={styles.sentence}>
            {cards[step].sentence}
          </p>
        </div>
      </div>
      {console.log(cardsForRepeat)}
      <div className={styles.buttons}>
        <button className={styles.showAnswerBtn}>Показать ответ</button>
      </div>
    </div>
  );
}
