import { useState } from 'react';
import styles from './CardRepeater.module.scss'

export default function ({ cards }) {

  let cardsForRepeat = [];
  let [step, setStep] = useState(0);

  cards.map(card => {
    if (card.repeatTime > -1)
      cardsForRepeat = [...cardsForRepeat, card];

    console.log(cardsForRepeat);
  });

  const showNextCard = () => {
    setStep(step++);
  }

  return (
    <div>
      {console.log(cardsForRepeat[0])}
      <div className={styles.card}>
        <div className={styles.textTop}>
          <p className={styles.word}>{cardsForRepeat[step].word}</p>
          <p className={styles.wordTranslation}>{cardsForRepeat[step].wordTranslation}</p>
        </div>

        <div className={styles.img}>
          {<img src={cardsForRepeat[step].imgSrc} />}
        </div>

        <div className={styles.textBottom}>
          <p className={styles.sentence}>
            {cardsForRepeat[step].sentence}
          </p>
        </div>
      </div>

      <div className={styles.buttons}>
        <button className={styles.showAnswerBtn} onClick={showNextCard}>Показать ответ</button>
      </div>
    </div>
  );
}
