import style from "./MainPage.module.scss";
import { Header } from "@components/index";

import MainMenuElement from "./MainMenuElement/MainMenuElement";
import MainUserInfo from "./MainUserInfo/MainUserInfo";
import MainAuthInfo from "./MainAuthInfo/MainAuthInfo";

// interface MainMenuPageTypes {
//   isAuth: boolean;
//   setIsAuth: (value: boolean) => void;
// }

const MainMenuPage = ({ isAuth = false, setIsAuth = () => {} }) => {
  return (
    <div>
      <Header />
      <div className={style.pageContainer}>
        {isAuth ? <MainUserInfo /> : <MainAuthInfo setIsAuth={setIsAuth} />}

        <div className={style.boxesContainer}>
          <MainMenuElement
            textContent="Повторить слова"
            imgContent="1.png"
            link="/cards-repeater"
          />
          <MainMenuElement
            textContent="Словарь"
            imgContent="12.png"
            link="/cards-list"
          />
          <MainMenuElement
            textContent="Алфавит"
            imgContent="3.png"
            link="/alphabet"
          />
          <MainMenuElement
            textContent="Грамматика"
            imgContent="2.png"
            link="/grammar"
          />
        </div>
      </div>
    </div>
  );
};

export default MainMenuPage;
