export default function({ card, step, setStep, onRoute, setOnRoute }) {

  let startRoute = () => {
    setStep(card.id - 1);
    setOnRoute(true);
  }

  return (
        <div className='card' key={ card.id }>
          <div className='card__img'>
          <img src={ card.imgSrc }/>
          </div>
          <div className='card__text'>
            <p className='card__word'>{ card.word }</p>
            <p className='card__translation'>{ card.wordTranslation }</p>
            <p className='card__sentence'>{ card.sentence }</p>
          </div>
          <button className='card__btn' onClick={startRoute}>OPEN</button>
        </div>
  );
}
