import { useState } from 'react';
import styles from './CardRepeater.module.scss';
import { useNavigate } from 'react-router-dom';

export default function ({ cards }) {

  const navigate = useNavigate();

  let cardsForRepeat = [];
  let [step, setStep] = useState(0);

  cards.map(card => {
    if (card.repeatTime > -1)
      cardsForRepeat = [...cardsForRepeat, card.id];

    console.log(cardsForRepeat);
  });

  const showNextCard = () => {
    if (step < cardsForRepeat.length - 1)
      setStep(++step);
    else
      navigate('/');
  }

  return (
    <div>

      <div className={styles.card}>
        <div className={styles.textTop}>
          <p className={styles.word}>{cards[cardsForRepeat[step] - 1].word}</p>
          <p className={styles.wordTranslation}>{cards[cardsForRepeat[step] - 1].wordTranslation}</p>
        </div>

        <div className={styles.img}>
          {<img src={cards[cardsForRepeat[step] - 1].imgSrc} />}
        </div>

        <div className={styles.textBottom}>
          <p className={styles.sentence}>
            {cards[cardsForRepeat[step] - 1].sentence}
          </p>
        </div>
      </div>
      <div className={styles.buttons}>
        <button className={styles.showAnswerBtn} onClick={showNextCard}>Показать ответ</button>
      </div>
    </div>
  );
}
